mapper.insert:
insert into log (id, event, event_description, 
  in_time, target, target_id, 
  user_id, after_content, before_content
  )
values (#{id,jdbcType=INTEGER}, #{event,jdbcType=VARCHAR}, #{eventDescription,jdbcType=VARCHAR}, 
  #{inTime,jdbcType=TIMESTAMP}, #{target,jdbcType=VARCHAR}, #{targetId,jdbcType=INTEGER}, 
  #{userId,jdbcType=INTEGER}, #{afterContent,jdbcType=LONGVARCHAR}, #{beforeContent,jdbcType=LONGVARCHAR}
  )

mapper.select:
select , 
from log where user_id = #{userId}

  order by ${orderBy}


  limit #{pageNo}, #{pageSize}

select l.* from log l left join user u on l.user_id = u.id

  order by ${orderBy}


  limit #{pageNo}, #{pageSize}
